package Lec_1;

public class Arithematic_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=7;
		int b=9;
		int c=a+b;
		System.out.println(c);
		c=a-b;
		System.out.println(c);
		int d=a*b;
		System.out.println(d);
		int e=a/b;
		System.out.println(e);
		d=a%b;
		System.out.println(d);
	}

}
